      return [...prevChats, chat]
    })
  }

  const handleOpen = () => {
    setOpen(true)
  };

  const handleClose = useCallback(() => {
    setOpen(false)
  }, [setOpen]);

  useEffect(() => {
    const initAnswer = "";
