import React from 'react'
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import Avatar from '@material-ui/core/Avatar';
import NoProfile from '../assets/img/no-profile.jpg';
import Road from '../assets/img/road.jpg';

const Chat = (chatTxt) => {
  const inQuestion = (chatTxt.type === 'question');
  const classes = inQuestion ? 'p-chat__row' : 'p-chat__reverse';


  return(
    <ListItem className={classes}>
    <ListItemAvatar>
      {isQuestion ? (
        <Avatar alt="icon" src="../assets/img/no-profile.jpg" />
        ) : (
        <Avatar alt="icon" src="../assets/img/road.jpg" /> 
      )}
    </ListItemAvatar>
  <div className="p-chat__bubble">{chatTxt.text}</div>
  </ListItem>
  )

}

export default Chat
