import React, {useState, useEffect, useCallback} from 'react';
import defaultDataset from "./dataset";
import './assets/styles/style.css'
import { AnswersList, Chats } from "./components/index"
import FormDialog from './components/Forms/FormDialog';


const App = () => {
    const [answers, setAnswers] = useState([]);
    const [chats, setChats] = useState([]);
    const [currentId, setCurrentId] = useState("init");
    const [open, setOpen] = useState(false);
    const dataset= useState(defaultDataset);


  const displayNextQuestion = (nextQuestionId, nextDataset) => {
      addChats({
        text: nextDataset.question,
        type: 'question'
      })

        setAnswers(nextDataset.answers)
        setCurrentId(nextQuestionId)
    }

  const selectAnswer = (selectedAnswer, nextQuestionId) => {
    switch(true) {

      case (nextQuestionId === 'contact'):
        handleOpen();
        break;

      case (/^https:*/.test(nextQuestionId)):
        const a = document.createElement('a');
        a.href = nextQuestionId;
        a.target = '_blank';
        a.click();
        break;

      default:
        addChats({ 
        text: selectedAnswer,
        type: 'answer'
        })

        setTimeout(() => displayNextQuestion(nextQuestionId, dataset[nextQuestionId]), 1200) //先程作成した関数を呼び出す引数は()内
        break;
    }
  }

  const addChats = (chat)　=> {
    setChats(prevChats => {
      return [...prevChats, chat]
    })
  }

  const handleOpen = () => {
    setOpen(true)
  };

  const handleClose = useCallback(() => {
    setOpen(false)
  }, [setOpen]);

  useEffect(() => {
        const initAnswer = "";
        selectAnswer(initAnswer, currentId)
},[]);


  useEffect(() => {
    const scrollArea = document.getElementById('scroll-area')
    if(scrollArea) {
      scrollArea.scrollTop = scrollArea.scrollHeight
    }
  })


  return (
    <section className="c-section">
      <div className="c-box">
        <Chats chats={chats} />
        <AnswersList answers={answers} select={selectAnswer}
          //select={this.selectAnswer()} <=()は無し[ついてるとrender毎にコールバック関数が走る為]}
        />
        <FormDialog　opne={open} handleOpen={handleOpen} handleClose={handleClose} />
      </div>
    </section>
    );
}

export default App
